mapper.update:
UPDATE CLASS SET
	CLASS_NM = #{class_nm},
	TEACHER = #{teacher}
WHERE CLASS_ID = #{class_id}

update.foreach:
UPDATE STUENDTTB SET
	STDNAME = #{item.STDNAME}
WHERE CLASSID = #{item.class_id}
AND STDNUMBER = #{item.STDNUMBER}

insert.foreach:
INSERT INTO STUENDTTB(
	CLASSID,
	STDNUMBER,
	STDNAME
) VALUES (
	#{item.class_id},
	(SELECT NVL(MAX(STDNUMBER),0)+1 FROM STUENDTTB S WHERE CLASSID = #{item.class_id}),
	#{item.STDNAME}
)

delete.foreach:
DELETE FROM STUENDTTB
WHERE CLASSID = #{item.class_id}
AND STDNUMBER = #{item.STDNUMBER}

mapper.insert:
INSERT INTO CLASS(
	CLASS_ID,
	CLASS_NM,
	TEACHER
) VALUES (
	#{class_id},
	#{class_nm},
	#{teacher}
)
